import { createContext, useState, useEffect } from "react";
const PortfolioContext = createContext();

export const PortfolioProvider = ({ children }) => {
    const [resume, setResume] = useState(false)
    const [contact, setContact] = useState(false)
    const [yoodlize, setYoodlize] = useState(false)
    const [techBlog, setTechBlog] = useState(false)
    const [wedding, setWedding] = useState(false)
    const [date, setDate] = useState(false)
    const [blackjack, setBlackjack] = useState(false)
    const [weather, setWeather] = useState(false)
    const [projects, setProjects] = useState(false)
    const [about, setAbout] = useState(false)


//  useEffect(()=> setAbout(true), [])

 const handleContact =() => {
    setContact(true)
    setYoodlize(false)
    setTechBlog(false)
    setWedding(false)
    setDate(false)
    setBlackjack(false)
    setWeather(false)
    setProjects(false)
    setAbout(false) 
    setResume(false)
 }
 const handleResume =() => {
  setContact(false)
  setYoodlize(false)
  setTechBlog(false)
  setWedding(false)
  setDate(false)
  setBlackjack(false)
  setWeather(false)
  setProjects(false)
  setAbout(false) 
  setResume(true)
  console
}
 const handleYoodlize =() => {
  setContact(false)
  setYoodlize(false)
  setTechBlog(false)
  setWedding(true)
  setDate(false)
  setBlackjack(false)
  setWeather(false)
  setProjects(false)
  setAbout(false)
  setResume(false)

}
const handleWedding =() => {
  setContact(false)
  setYoodlize(true)
  setTechBlog(false)
  setWedding(false)
  setDate(false)
  setBlackjack(false)
  setWeather(false)
  setProjects(false)
  setAbout(false)
  setResume(false)

}

return (
    <PortfolioContext.Provider
      value={{ 
        resume,
        contact,
        yoodlize,
        techBlog,
        wedding,
        date,
        blackjack,
        weather,
        projects,
        about,
        setResume,
        setContact,
        setYoodlize,
        setTechBlog,
        setWedding,
        setDate,
        setBlackjack,
        setWeather,
        setProjects,
        setAbout,
        handleContact,
        handleYoodlize,
        handleWedding,
        handleResume
      }}
      >
      {children}
       </PortfolioContext.Provider>
)
    }
    export default PortfolioContext;
